import React, { useState } from 'react';
import StaffModal from './StaffModal';

const Contact = () => {
  const [showStaffModal, setShowStaffModal] = useState(false);
  const [selectedStaff, setSelectedStaff] = useState(null);

  // Function to handle opening the staff modal
  const handleOpenStaffModal = (staff) => {
    setSelectedStaff(staff);
    setShowStaffModal(true);
  };

  // Function to handle closing the staff modal
  const handleCloseStaffModal = () => {
    setSelectedStaff(null);
    setShowStaffModal(false);
  };

  // Render staff list with click event to open the modal
  return (
    <div>
      {/* Render staff list */}
        {staffMembers.map((staff, index) => (
          <div key={index} onClick={() => handleOpenStaffModal(staff)}>
            <p>{staff.name}</p>
            <p>{staff.position}</p>
            <p>{staff.contactNo}</p>
          </div>
        ))}


      {/* Render StaffModal */}
      <StaffModal
        showStaffModal={showStaffModal}
        handleCloseStaffModal={handleCloseStaffModal}
        staff={selectedStaff}
      />
    </div>
  );
};

export default Contact;
