import React, { useState } from "react";
import "../CSS/CreateRequest.css";
import Rating from "react-rating-stars-component";

const CreateRequest = () => {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    mob: "",
    job: "",
    ans: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleRatingChange = (newRating) => {
    setFormData((prevState) => ({
      ...prevState,
      rating: newRating, 
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your form submission logic here
    console.log(formData);
  };

  return (
    <div className="createContainer">
      <div className="container-fluid px-1 py-5 mx-auto ">
        <div className="row d-flex justify-content-center ">
          <div className="col-xl-7 col-lg-8 col-md-9 col-11 text-center">
            <h3>Feedback</h3>
            <p className="blue-text">Please give the feedback</p>
            <div className="Createcard">
              <h5 className="text-center mb-4">
                Hostel Management Feedback Form
              </h5>
              <form className="form-card" onSubmit={handleSubmit}>
                <div className="row justify-content-between text-left"></div>
                <div className="row justify-content-center text-left">
                  <div className="form-group col-sm-6 flex-column d-flex">
                    <label className="form-control-label px-3">
                      Feedback<span className="text-danger"> *</span>
                    </label>
                    <input
                      type="text"
                      name="job"
                      value={formData.job}
                      onChange={handleChange}
                      className="form-control"
                      placeholder="Enter reason"
                    />
                  </div>
                </div>

                <br />
                <div className="row justify-content-center text-left">
                  <div className="form-group col-sm-6 flex-column d-flex">
                    <label className="form-control-label px-3">
                      Rating<span className="text-danger"> *</span>
                    </label>
                    {/* Render the rating component */}
                    <Rating
  count={5} // Set count to 5 for 5 stars
  isHalf={false} // Set isHalf to false
  edit={true} // Allow editing
  onChange={handleRatingChange} // Callback function to handle rating change
  value={formData.rating} // Current rating value
  size={30} // Size of the stars
  activeColor="#ffd700" // Color of active stars
/>

                  </div>
                </div>
                <div className="row justify-content-center">
                  <div className="form-group col-sm-6">
                    <button type="submit" className="btn-block btn-primary">
                      Create Request
                    </button>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateRequest;
