import React, { useState } from 'react';
import '../CSS/Contact.css';
import StaffModal from './StaffModal'; 

const Contact = () => {
    const [showStaffModal, setShowStaffModal] = useState(false);
    const [selectedStaff, setSelectedStaff] = useState(null);


    const handleOpenStaffModal = (staff) => {
      setSelectedStaff(staff);
      setShowStaffModal(true);
    };


    const handleCloseStaffModal = () => {
      setSelectedStaff(null);
      setShowStaffModal(false);
    };


  const staffMembers = [
    { name: 'Sudha Vashishta', position: 'Owner', contactNo: '123-456-7890' },
    { name: 'Shobha Sharma', position: 'Warden', contactNo: '987-654-3210' },
    { name: 'Vishal Thakur', position: 'Cook', contactNo: '123-456-7890' },
    { name: 'Vinay Sharma', position: 'Cook', contactNo: '987-654-3210' },
    { name: 'Rishabh Thakur', position: 'Manager', contactNo: '123-456-7890' },
    { name: 'Ganesh Ravade', position: 'Assistant Manager', contactNo: '987-654-3210' },
    { name: 'Jayesh Karma',  position: 'HouseKeeper', contactNo: '123-996-7890' },
    { name: 'Harshit Sitole', position: 'Security Gaurd', contactNo: '987-654-0010' },
  ];


  return (
    <div>
      <div className="contactContainer">
        <h1>Contact Us</h1>
        <p>Feel free to contact any of our staff members for assistance:</p>
        <div className="staffList">
          {staffMembers.map((staff, index) => (
            <div key={index} className="staffMember" onClick={() => handleOpenStaffModal(staff)}>
              <h2>{staff.name}</h2>
              <p>{staff.position}</p>
              <p>Contact No: {staff.contactNo}</p>
            </div>
          ))}
        </div>
      </div>

       {/* Render StaffModal if selectedStaff is not null */}
       {/* Render selected staff member's description */}
      {selectedStaff && (
        <div className="staff-description">
          <h2>{selectedStaff.name}</h2>
          <p>{selectedStaff.description}</p>
        </div>
      )}
    </div>
  );
};

export default Contact;
