import React, { useState } from "react";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../CSS/DocumentUpload.css';

const DocumentUpload = ({ handleClose }) => {
  const [documents, setDocuments] = useState([]);

  const handleUpload = (event) => {
    const files = event.target.files;
    const uploadedDocuments = Array.from(files);
    setDocuments([...documents, ...uploadedDocuments]);
  };

  const handleRemove = (index) => {
    const updatedDocuments = [...documents];
    updatedDocuments.splice(index, 1);
    setDocuments(updatedDocuments);
  };

  const handleUploadAll = () => {
    // Simulate uploading all files
    // In a real application, you would send these files to your server
    // and handle the upload process there
    console.log("Uploading all files:", documents);

    // Show toaster notification
    toast.success("Files uploaded successfully!");

    // Clear the uploaded documents
    setDocuments([]);
  };

  const handleCloseClick = () => {
    handleClose();
  };

  return (
    <div className="document-upload-modal">
      <div className="overlay-content">
        <h2>Document Upload</h2>
        <input type="file" onChange={handleUpload} multiple />
        <div className="uploaded-documents">
          {documents.length === 0 ? (
            toast.failure("Files uploaded successfully!"),
            <div className="no-file-chosen">No file chosen</div>
          ) : (
            documents.map((document, index) => (
              <div key={index} className="document">
                <span>{document.name}</span>
                <button onClick={() => handleRemove(index)}>Remove</button>
              </div>
            ))
          )}
        </div>
        <div className="button-container">
          <button onClick={handleUploadAll} className="upload-button">Upload All</button>
          <button onClick={handleCloseClick} className="close-button">Close</button>
        </div>
      </div>
    </div>
  );
};

export default DocumentUpload;
