import React, { useState } from 'react';
import '../CSS/Contact.css';
import StaffModal from './StaffModal'; // Import the modal component

const Contact = () => {
  const [selectedStaff, setSelectedStaff] = useState(null); // State to store the selected staff member

  const staffMembers = [
    { name: 'Sudha Vashishta', position: 'Owner', contactNo: '123-456-7890' },
    { name: 'Shobha Sharma', position: 'Warden', contactNo: '987-654-3210' },
    { name: 'Vishal Thakur', position: 'Cook', contactNo: '123-456-7890' },
    { name: 'Vinay Sharma', position: 'Cook', contactNo: '987-654-3210' },
    { name: 'Rishabh Thakur', position: 'Manager', contactNo: '123-456-7890' },
    { name: 'Ganesh Ravade', position: 'Assistant Manager', contactNo: '987-654-3210' },
    { name: 'Jyesh', position: 'Manager', contactNo: '123-456-7890' },
    { name: 'Ganesh Ravade', position: 'Assistant Manager', contactNo: '987-654-3210' },
  ];

  // Function to handle when a staff member is clicked
  const handleStaffClick = (staff) => {
    setSelectedStaff(staff); // Set the selected staff member in state
  };

  return (
    <div>
      <div className="contactContainer">
        <h1>Contact Us</h1>
        <p>Feel free to contact any of our staff members for assistance:</p>
        <div className="staffList">
          {staffMembers.map((staff, index) => (
            <div key={index} className="staffMember" onClick={() => handleStaffClick(staff)}>
              <h2>{staff.name}</h2>
              <p>{staff.position}</p>
              <p>Contact No: {staff.contactNo}</p>
            </div>
          ))}
        </div>
      </div>
      {/* Render the modal if a staff member is selected */}
      {selectedStaff && <StaffModal staff={selectedStaff} onClose={() => setSelectedStaff(null)} />}
    </div>
  );
};

export default Contact;
