import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import axios from "axios"; // Import axios
import UpdateDetail from './UpdateDetail'; // Import UpdateDetail component

const SignUp = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    contact: "",
    address: "",
    agreeTerms: false,
  });

  const [registered, setRegistered] = useState(false);

  useEffect(() => {
    if (registered) {
      const timeout = setTimeout(() => {
        navigate("/login");
      }, 3000); 
      return () => clearTimeout(timeout);
    }
  }, [registered, navigate]);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {

    e.preventDefault();


    if (
      formData.name === "" ||
      formData.email === "" ||
      formData.password === "" ||
      formData.confirmPassword === "" ||
      formData.contact === "" ||
      formData.address === ""
    ) {

      toast.error("Please fill all required fields");
      return;
    }

    try {

      setRegistered(true);

      toast.success("Registered Successfully!");
    } catch (error) {
      console.error("Error occurred:", error);
      toast.error("An error occurred. Please try again.");
    }
  };

  return (
    <section className="vh-100" style={{ backgroundColor: "#eee" }}>
      <div className="container h-100">
        <section className="vh-100" style={{ backgroundColor: "#eee" }}>
          <div className="container h-100">
            <div className="row d-flex justify-content-center align-items-center h-100">
              <div className="col-lg-12 col-xl-11">
                <div
                  className="card text-black"
                  style={{ borderRadius: "25px" }}
                >
                  <div className="card-body p-md-5">
                    <div className="row justify-content-center">
                      <div className="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">
                        <p className="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">
                          Hostel Management Sign up
                        </p>
                        <form className="mx-1 mx-md-4" onSubmit={handleSubmit}>
                          {/* Your form inputs */}
                        </form>
                        {/* Render UpdateDetail component passing formData as props */}
                        <UpdateDetail show={registered} handleClose={() => setRegistered(false)} formData={formData} />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <ToastContainer />
        </section>
      </div>
      <ToastContainer />
    </section>
  );
};

export default SignUp;
