import React from 'react';
import '../CSS/MessDetail.css';

const menu = {
  Monday: ["Breakfast: Masala Dosa with Sambar and Chutney", "Lunch: Rajma Chawal", "Dinner: Palak Paneer (Spinach with Cottage Cheese)"],
  Tuesday: ["Breakfast: Poha (Flattened Rice)", "Lunch: Chole Bhature (Chickpea Curry with Fried Bread)", "Dinner: Vegetable Biryani"],
  Wednesday: ["Breakfast: Aloo Paratha with Curd", "Lunch: Dal Tadka (Lentil Curry) with Jeera Rice", "Dinner: Paneer Butter Masala with Naan"],
  Thursday: ["Breakfast: Idli with Coconut Chutney and Sambar", "Lunch: Baingan Bharta (Roasted Eggplant Curry) with Roti", "Dinner: Chicken Curry with Steamed Rice"],
  Friday: ["Breakfast: Upma (Semolina Porridge)", "Lunch: Aloo Gobi (Potato and Cauliflower Curry) with Chapati", "Dinner: Tandoori Chicken with Mint Chutney"],
  Saturday: ["Breakfast: Poori Bhaji (Fried Bread with Potato Curry)", "Lunch: Veg Pulao with Raita", "Dinner: Mutton Rogan Josh with Garlic Naan"],
  Sunday: ["Breakfast: Medu Vada with Coconut Chutney", "Lunch: Chicken Biryani", "Dinner: Vegetable Korma with Paratha"]
};

const MessDetail = () => {
  return (
    <div className="menu-container">
      <h2>Mess Menu</h2>
      <div className="menu-list">
        {Object.keys(menu).map((day, index) => (
          <div key={index} className="menu-item">
            <h3>{day}</h3>
            <ul>
              {menu[day].map((dish, idx) => (
                <li key={idx}>{dish}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MessDetail;
