import React from 'react';
import '../CSS/MessDetail.css';

const menu = {
  Monday: ["Breakfast: Toast and Eggs", "Lunch: Grilled Chicken with Salad", "Dinner: Spaghetti with Meatballs"],
  Tuesday: ["Breakfast: Oatmeal with Fruits", "Lunch: Fish Tacos", "Dinner: Vegetable Stir-Fry"],
  Wednesday: ["Breakfast: Pancakes with Syrup", "Lunch: Beef Stew", "Dinner: Roast Chicken with Vegetables"],
  Thursday: ["Breakfast: Yogurt with Granola", "Lunch: Veggie Burger with Fries", "Dinner: Shrimp Alfredo Pasta"],
  Friday: ["Breakfast: Bagel with Cream Cheese", "Lunch: BBQ Ribs", "Dinner: Pizza"],
  Saturday: ["Breakfast: Scrambled Eggs with Bacon", "Lunch: Tuna Sandwich", "Dinner: Sushi"],
  Sunday: ["Breakfast: Waffles", "Lunch: Chicken Caesar Salad", "Dinner: Lasagna"]
};

const MessDetail = () => {
  return (
    <div className="menu-container">
      <h2>Mess Menu</h2>
      <div className="menu-list">
        {Object.keys(menu).map((day, index) => (
          <div key={index} className="menu-item">
            <h3>{day}</h3>
            <ul>
              {menu[day].map((dish, idx) => (
                <li key={idx}>{dish}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MessDetail;
