// SignUp.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { API_BASE_URL } from '../HomeComponent/apiConfig';

const SignUp = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    cno: '',
    address: '',
    agreeTerms: false,
  });

  const [registered, setRegistered] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (Object.values(formData).some((field) => field === '') || !formData.agreeTerms) {
      toast.error('Please fill all required fields and agree to terms');
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/rails/user_profile/user`, formData);

      if (response.status === 200) {
        setRegistered(true);
        toast.success('Registered Successfully!');
        setFormData({
          name: '',
          email: '',
          password: '',
          confirmPassword: '',
          cno: '',
          address: '',
          agreeTerms: false,
        });
      }
    } catch (error) {
      console.error('Error occurred:', error);
      toast.error('An error occurred. Please try again.');
    }
  };

  const handleLoginClick = () => {
    navigate('/login');
  };

  return (
    <section className="vh-100" style={{ backgroundColor: '#eee' }}>
      <div className="container h-100">
        <div className="row d-flex justify-content-center align-items-center h-100">
          <div className="col-lg-12 col-xl-11">
            <div className="card text-black" style={{ borderRadius: '25px' }}>
              <div className="card-body p-md-5">
                <div className="row justify-content-center">
                  <div className="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">
                    <p className="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">
                      Hostel Management Sign up
                    </p>
                    <form className="mx-1 mx-md-4" onSubmit={handleSubmit}>
                      {/* Your form inputs here */}
                      <button type="submit" className="btn btn-primary btn-lg">
                        Register
                      </button>
                      <button type="button" className="btn btn-secondary btn-lg" onClick={handleLoginClick}>
                        Login
                      </button>
                    </form>
                  </div>
                  {/* Image */}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* Show UpdateDetails modal when registered state is true */}
      {registered && <UpdateDetails handleClose={() => setRegistered(false)} formData={formData} />}
      <ToastContainer />
    </section>
  );
};

export default SignUp;
