import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import axios from "axios";
import UpdateDetails from "../HomeComponent/UpdateDetails";

const SignUp = () => {
  const navigate = useNavigate();

  // State to store form data
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    contact: "",
    address: "",
    agreeTerms: false,
  });

  // State to track if registration is successful
  const [registered, setRegistered] = useState(false);

  // Effect to navigate to login page after successful registration
  useEffect(() => {
    if (registered) {
      const timeout = setTimeout(() => {
        navigate("/login");
      }, 3000);
      return () => clearTimeout(timeout);
    }
  }, [registered, navigate]);

  // Handler for input change
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  // Handler for form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if all required fields are filled
    if (
      Object.values(formData).some((field) => field === "") ||
      !formData.agreeTerms
    ) {
      toast.error("Please fill all required fields and agree to terms");
      return;
    }

    try {
      // Perform registration
      // Here you would typically make an API call to register the user
      // For now, we'll just simulate a successful registration
      setRegistered(true);
      toast.success("Registered Successfully!");
    } catch (error) {
      console.error("Error occurred:", error);
      toast.error("An error occurred. Please try again.");
    }
  };

  return (
    <section className="vh-100" style={{ backgroundColor: "#eee" }}>
      <div className="container h-100">
        <section className="vh-100" style={{ backgroundColor: "#eee" }}>
          <div className="container h-100">
            <div className="row d-flex justify-content-center align-items-center h-100">
              <div className="col-lg-12 col-xl-11">
                <div
                  className="card text-black"
                  style={{ borderRadius: "25px" }}
                >
                  <div className="card-body p-md-5">
                    <div className="row justify-content-center">
                      <div className="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">
                        <p className="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">
                          Hostel Management Sign up
                        </p>
                        <form className="mx-1 mx-md-4" onSubmit={handleSubmit}>
                          {/* Form inputs */}
                          <div className="mb-4 form-check">
                            <input
                              className="form-check-input"
                              type="checkbox"
                              id="agreeTerms"
                              name="agreeTerms"
                              checked={formData.agreeTerms}
                              onChange={handleInputChange}
                            />
                            <label
                              className="form-check-label"
                              htmlFor="agreeTerms"
                            >
                              I agree to all statements in{" "}
                              <a href="#!">Terms of service</a>
                            </label>
                          </div>
                          <div className="d-grid">
                            <button
                              type="submit"
                              className="btn btn-primary btn-lg"
                            >
                              Register
                            </button>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <ToastContainer />
        </section>
      </div>
      {/* Show UpdateDetails modal when registered state is true */}
      {registered && (
        <UpdateDetails
          show={registered}
          handleClose={() => setRegistered(false)}
          formData={formData}
        />
      )}
      <ToastContainer />
    </section>
  );
};

export default SignUp;
