import React from 'react';
import { Modal, Button } from 'react-bootstrap';

const RoomDetails = ({ showModal, setShowModal }) => {
  // Function to close the modal
  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <Modal show={showModal} onHide={handleCloseModal} centered>
      <Modal.Header closeButton>
        <Modal.Title>Room Details</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* Replace this with actual room details */}
        <p>Room Type: Single Seater</p>
        <p>Fees     : $</p>
        <p>Capacity: 1 person</p>
        <p>Description: Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla vestibulum tristique dolor, eget accumsan odio consequat eu.</p>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleCloseModal}>
          Close
        </Button>
        <Button variant="primary">Book Room</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default RoomDetails;
