import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios'; // Import axios
import { useEffect } from 'react';

const SignUp = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
        agreeTerms: false
    });

    const [registered, setRegistered] = useState(false);

    useEffect(() => {
        if (registered) {
            const timeout = setTimeout(() => {
                navigate('/login');
            }, 3000); // Redirect after 3 seconds
            return () => clearTimeout(timeout);
        }
    }, [registered, navigate]);

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = async (e) => { // Make the function asynchronous
        e.preventDefault();

        if (formData.name === '' || formData.email === '' || formData.password === '' || formData.confirmPassword === '') {
            toast.error('Please fill all required fields');
            return;
        }

        try {
            // Define your API endpoint URL
            const apiUrl = 'YOUR_API_ENDPOINT_URL'; // Replace with your actual API endpoint URL

            // Make a POST request to the API endpoint with the form data
            const response = await axios.post(apiUrl, formData);

            // Check the response status
            if (response.status === 200) {
                console.log(response.data); // Log the response data (optional)
                toast.success("Registered Successfully!");
                setRegistered(true);
            } else {
                toast.error("Registration failed. Please try again.");
            }
        } catch (error) {
            console.error('Error occurred:', error);
            toast.error("An error occurred. Please try again.");
        }
    };

    return (
        <section className="vh-100" style={{ backgroundColor: "#eee" }}>
            <div className="container h-100">
                {/* Rest of your component code */}
            </div>
            <ToastContainer />
        </section>
    );
}

export default SignUp;
