import React, { useState, useEffect } from 'react';
import singleSeaterImage from '../Images/img7.jpg';
import doubleSeaterImage from '../Images/img3.jpg';
import threeSeaterImage from '../Images/img4.jpg';
import fourSeaterImage from '../Images/img5.jpg';
import axios from 'axios';

const RoomDetailsPage = () => {
  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    // Fetch data from the API
    axios.get('https://ee2c-125-99-200-202.ngrok-free.app/rails/user_profile/rooms/room/1')
      .then(response => {
        // Update state with the fetched data
        setRooms(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="container">
      <h2 className="mt-5 mb-4">Room Details</h2>
      <div className="row">
        {rooms.map((room, index) => (
          <div key={index} className="col-md-6 mb-4">
            <div className="card h-100">
              <img src={getImageForRoomType(room.type)} className="card-img-top" alt={room.type} />
              <div className="card-body">
                <h4 className="card-title">{room.type}</h4>
                <h5 className="card-title">{room.rent}</h5>
                <p className="card-text">{room.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Function to get image based on room type
const getImageForRoomType = (type) => {
  switch (type) {
    case 'Single Seater':
      return singleSeaterImage;
    case 'Double Seater':
      return doubleSeaterImage;
    case 'Three Seater':
      return threeSeaterImage;
    case 'Four Seater':
      return fourSeaterImage;
    default:
      return '';
  }
};

export default RoomDetailsPage;
